import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, GeoJSON } from 'react-leaflet';

const WorldMap = () => {
  const [geoJsonData, setGeoJsonData] = useState(null);

  useEffect(() => {
    fetch('/data/custom.geo.json')
      .then(response => response.json())
      .then(data => setGeoJsonData(data))
      .catch(err => console.error('Error loading GeoJSON:', err));
  }, []);

  return (
    <MapContainer
    className="map-container"
    center={[20, 0]} // Centered on the map
    zoom={3} // Increase zoom level for a closer view
    zoomControl={false}
    dragging={false}
    doubleClickZoom={false}
    scrollWheelZoom={false}
    touchZoom={false}
    keyboard={false}
    attributionControl={false}
    worldCopyJump={false}
    >
      <TileLayer
  url="https://stamen-tiles.a.ssl.fastly.net/toner/{z}/{x}/{y}.png"
  attribution='&copy; <a href="https://stamen.com/">Stamen Design</a> &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
/>
      {geoJsonData && (
        <GeoJSON
          data={geoJsonData}
          style={{
            color: "#005bff",
            weight: 1,
            fillColor: "#a3caff",
            fillOpacity: 0.5
          }}
        />
      )}
    </MapContainer>
  );
};

export default WorldMap;
