import React, { useEffect, useState } from 'react';
import Plot from 'react-plotly.js';
import Papa from 'papaparse';

const WorldMap = ({ dataType, onCountrySelect }) => {
  const [geoData, setGeoData] = useState([]);

  useEffect(() => {
    const fileName = dataType === 'lung' 
      ? '/data/lung_cancer_prediction_dataset.csv'
      : '/data/thyroid_cancer_risk_data.csv';

    Papa.parse(fileName, {
      download: true,
      header: true,
      complete: (result) => setGeoData(result.data),
      error: (error) => console.error('Error loading CSV:', error),
    });
  }, [dataType]);

  const filteredData = geoData.map((item) => ({
    country: item.country || item.Country,
    value: parseFloat(item[dataType === 'lung' ? 'Lung Cancer Rate' : 'Thyroid Cancer Rate']) || 0,
  }));

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <Plot
        data={[
          {
            type: 'choropleth',
            locationmode: 'country names',
            locations: filteredData.map((item) => item.country),
            z: filteredData.map((item) => item.value),
            colorscale: 'Reds',
            colorbar: { title: dataType === 'lung' ? 'Lung Cancer Rate' : 'Thyroid Cancer Rate' }
          }
        ]}
        layout={{
          geo: {
            projection: { type: 'natural earth' },
            showland: true,
            landcolor: '#E5E5E5',
            countrycolor: '#B3B3B3',
            showlakes: true,
            lakecolor: '#FFFFFF',
            scrollZoom: true,
          },
          title: `Global ${dataType === 'lung' ? 'Lung Cancer' : 'Thyroid Cancer'} Data`,
          autosize: true,
          margin: { t: 50, b: 0, l: 0, r: 0 },
        }}
        style={{ width: '100%', height: '100%' }}
        onClick={(event) => {
          const country = event?.points?.[0]?.location;
          if (country) {
            onCountrySelect(country);
          }
        }}
      />
    </div>
  );
};

export default WorldMap;
